      link:
        "https://web-release.com/zain-ksa-completes-major-digital-transformation-program-in-collaboration-with-netcracker",
      domain_url: "web-release.com",
      full_domain_url: "web-release.com",
      name_source: "Web Release",
      is_headline: false,
